local Players=game:GetService("Players")local RunService=game:GetService("RunService")local TweenService=game:GetService("TweenService")local VirtualInputManager=game:GetService("VirtualInputManager")local player=Players.LocalPlayer local gui=player:WaitForChild("PlayerGui")for _,name in {"RealESpammer","SpeedSetterGui","TeleportToolV2"}do local o=gui:FindFirstChild(name)if o then o:Destroy()end end local character=player.Character or player.CharacterAdded:Wait()local root=character:WaitForChild("HumanoidRootPart")local humanoid=character:WaitForChild("Humanoid")local function createPanel(t,sx,sy,px,py,bc,bg,ch)local s=Instance.new("ScreenGui")s.ResetOnSpawn=false s.Parent=gui local m=Instance.new("Frame")m.Size=UDim2.new(0,sx,0,45+ch)m.Position=UDim2.new(0,px,0,py)m.BackgroundColor3=bg m.BorderSizePixel=2 m.BorderColor3=bc m.Active=true m.Draggable=true m.Parent=s local bar=Instance.new("Frame")bar.Size=UDim2.new(1,0,0,45)bar.BackgroundColor3=Color3.fromRGB(40,40,40)bar.ZIndex=10 bar.Parent=m local title=Instance.new("TextLabel")title.Size=UDim2.new(1,-180,1,0)title.BackgroundTransparency=1 title.Text=t title.TextColor3=Color3.new(1,1,1)title.Font=Enum.Font.GothamBold title.TextSize=18 title.TextXAlignment=Enum.TextXAlignment.Left title.Parent=bar local arrow=Instance.new("TextButton")arrow.Size=UDim2.new(0,40,0,40)arrow.Position=UDim2.new(1,-90,0,2)arrow.BackgroundColor3=Color3.fromRGB(255,180,0)arrow.Text="▼"arrow.TextColor3=Color3.new(0,0,0)arrow.Font=Enum.Font.GothamBold arrow.TextSize=20 arrow.ZIndex=11 arrow.Parent=bar local close=Instance.new("TextButton")close.Size=UDim2.new(0,40,0,40)close.Position=UDim2.new(1,-45,0,2)close.BackgroundColor3=Color3.fromRGB(220,20,20)close.Text="x"close.TextColor3=Color3.new(1,1,1)close.Font=Enum.Font.GothamBold close.TextSize=22 close.ZIndex=11 close.Parent=bar local content=Instance.new("Frame")content.Size=UDim2.new(1,0,0,ch)content.Position=UDim2.new(0,0,0,45)content.BackgroundTransparency=1 content.ClipsDescendants=true content.Parent=m local min=false local full=ch arrow.MouseButton1Click:Connect(function()if min then TweenService:Create(content,TweenInfo.new(.3,Enum.EasingStyle.Sine),{Size=UDim2.new(1,0,0,full)}):Play()TweenService:Create(m,TweenInfo.new(.3,Enum.EasingStyle.Sine),{Size=UDim2.new(0,sx,0,45+full)}):Play()arrow.Text="▼"min=false else TweenService:Create(content,TweenInfo.new(.3,Enum.EasingStyle.Sine),{Size=UDim2.new(1,0,0,0)}):Play()TweenService:Create(m,TweenInfo.new(.3,Enum.EasingStyle.Sine),{Size=UDim2.new(0,sx,0,45)}):Play()arrow.Text="▲"min=true end end)close.MouseButton1Click:Connect(function()s:Destroy()end)return content,s end
local pContent=createPanel("LIVE POSITION",380,60,20,20,Color3.fromRGB(0,255,0),Color3.fromRGB(20,20,20),60)local pLabel=Instance.new("TextLabel")pLabel.Size=UDim2.new(1,-90,1,0)pLabel.Position=UDim2.new(0,10,0,5)pLabel.BackgroundTransparency=1 pLabel.Text="Position: Loading..."pLabel.TextColor3=Color3.fromRGB(0,255,0)pLabel.Font=Enum.Font.Code pLabel.TextSize=18 pLabel.Parent=pContent local copyBtn=Instance.new("TextButton")copyBtn.Size=UDim2.new(0,70,0,40)copyBtn.Position=UDim2.new(1,-80,0,10)copyBtn.BackgroundColor3=Color3.fromRGB(0,170,255)copyBtn.Text="COPY"copyBtn.Parent=pContent copyBtn.MouseButton1Click:Connect(function()if root then local p=root.Position setclipboard(string.format("%.1f, %.1f, %.1f",p.X,p.Y,p.Z))copyBtn.Text="OK"task.wait(1)copyBtn.Text="COPY"end end)RunService.Heartbeat:Connect(function()if root then pLabel.Text=string.format("Position: %.1f, %.1f, %.1f",root.Position.X,root.Position.Y,root.Position.Z)end end)
local tpContent=createPanel("TELEPORT PANEL",380,320,20,140,Color3.fromRGB(100,100,255),Color3.fromRGB(30,30,30),320)local inputBox=Instance.new("TextBox") inputBox.Text = "Enter - Cords "inputBox.Size=UDim2.new(0,250,0,40)inputBox.Position=UDim2.new(0,10,0,5)inputBox.PlaceholderText="X, Y, Z"inputBox.Parent=tpContent local addBtn=Instance.new("TextButton")addBtn.Size=UDim2.new(0,110,0,40)addBtn.Position=UDim2.new(0,270,0,5)addBtn.BackgroundColor3=Color3.fromRGB(0,200,0)addBtn.Text="ADD"addBtn.Parent=tpContent local scroll=Instance.new("ScrollingFrame")scroll.Size=UDim2.new(1,-20,1,-55)scroll.Position=UDim2.new(0,10,0,55)scroll.ScrollBarThickness=8 scroll.Parent=tpContent local layout=Instance.new("UIListLayout")layout.Padding=UDim.new(0,8)layout.Parent=scroll local function addEntry(pos,name)local e=Instance.new("Frame")e.Size=UDim2.new(1,0,0,60)e.BackgroundColor3=Color3.fromRGB(60,50,70)e.Parent=scroll local nb=Instance.new("TextBox")nb.Size=UDim2.new(0,150,.5,0)nb.Position=UDim2.new(0,10,0,14)nb.BackgroundTransparency=.5 nb.Text=name nb.TextColor3=Color3.fromRGB(255,255,100)nb.Parent=e local go=Instance.new("TextButton")go.Size=UDim2.new(0,100,0,40)go.Position=UDim2.new(0,170,0,10)go.BackgroundColor3=Color3.fromRGB(0,150,255)go.Text="TP"go.Parent=e go.MouseButton1Click:Connect(function()root.CFrame=CFrame.new(pos)end)local del=Instance.new("TextButton")del.Size=UDim2.new(0,40,0,40)del.Position=UDim2.new(1,-50,0,10)del.BackgroundColor3=Color3.fromRGB(220,20,20)del.Text="X"del.Parent=e del.MouseButton1Click:Connect(function()e:Destroy()scroll.CanvasSize=UDim2.new(0,0,0,layout.AbsoluteContentSize.Y)end)scroll.CanvasSize=UDim2.new(0,0,0,layout.AbsoluteContentSize.Y+10)end addBtn.MouseButton1Click:Connect(function()local t=inputBox.Text:gsub("%s","")local x,y,z=t:match("([%-%d%.]+),([%-%d%.]+),([%-%d%.]+)")if x then addEntry(Vector3.new(tonumber(x),tonumber(y),tonumber(z)),"Enter-Position-Name")inputBox.Text=""end end)player.CharacterAdded:Connect(function(c)character=c root=c:WaitForChild("HumanoidRootPart")humanoid=c:WaitForChild("Humanoid")end)print("TOOLKIT GELADEN – [X] + [▼] IMMER SICHTBAR!")
